		var option = node.getAttribute("Option");
		
		var model = tem.javascriptUtils.classFactory("tem.model." + option);
		
		if(option == "CustomRelevance"){
			model.textValue = node.text;
			model.relevance = node.text;
		}	
		
		return model;
